	var TOA_AD_STYLE = "position: absolute; top: 0;";
	var TOA_AD_HEIGHT = "100";
	var TOA_IS_ANIMATED = "true";
	var TOA_MOUSEOVER = "onBannerMouseOver(0, 976, 100, 0)";
	var TOA_MOUSEOUT = "onBannerMouseOut(0, 976, 100, 0)";
	var TOA_AKAMAI_PATH = getAdAkamaiPath();

	var TFSMFlash_OASCLICK="http%3A%2F%2Fwww.tetrisfriends.com%2Fshop%2Findex.php%3FshowPurchasePopup%3D1%26promoCode%3Djambool_discount_25%26ref%3Dfrom-strip";
	var TFSMFlash_SWFCLICKVARIABLE="?clickTAG="+TFSMFlash_OASCLICK+"&percentage=25";
	var TFSMFlash_SWFFILE="http://videoads.tetrisfriends.com/inHouse_ads/summerSpecial3_976x100.swf"+TFSMFlash_SWFCLICKVARIABLE;
	var TOA_CALLBACK_FUNC='swfobject.embedSWF("' + TFSMFlash_SWFFILE+ '", "home_custom_ad_content", 976, 100, "9.0.0", {}, {}, {wmode: "transparent"}, {allowscriptaccess: "always"}, {id:"customAdUnit", name:"customAdUnit"});';

	initPromoAd(TOA_AD_STYLE, TOA_AD_HEIGHT, TOA_IS_ANIMATED, TOA_MOUSEOVER, TOA_MOUSEOUT, TOA_CALLBACK_FUNC);

	try {
		pageTracker._trackPageview('/promotion/promo_strip_discount_25');
	} catch (err) {	}
});


