	var TOA_RIGHT_RAIL_BG     = "transparent";
	var TOA_RIGHT_RAIL_URL    = "/images/promo_ads/holidays/thanksgivingRailRight.png";
	var TOA_RIGHT_RAIL_REPEAT = "no-repeat";
	var TOA_RIGHT_RAIL_POS    = "-91,-214,232,847";
	var TOA_RAIL_CALLBACK     = "$('body').css('background', '#fff url(" + TOA_AKAMAI_PATH + "/images/promo_ads/holidays/thanksgivingGradient.jpg) repeat-x'); $('#home_ad_left_rail').css('z-index', '100');";

	var TOA_LEFT_RAIL_CONCAT  = TOA_LEFT_RAIL_BG + " url(" + TOA_AKAMAI_PATH + TOA_LEFT_RAIL_URL + ") " + TOA_LEFT_RAIL_REPEAT;
	var TOA_RIGHT_RAIL_CONCAT = TOA_RIGHT_RAIL_BG + " url(" + TOA_AKAMAI_PATH + TOA_RIGHT_RAIL_URL + ") " + TOA_RIGHT_RAIL_REPEAT;

	initRailAds(TOA_LEFT_RAIL_CONCAT, TOA_LEFT_RAIL_POS, TOA_RIGHT_RAIL_CONCAT, TOA_RIGHT_RAIL_POS, TOA_RAIL_CALLBACK);	

	var TOA_AD_STYLE = "";
	var TOA_AD_HEIGHT = "95";
	var TOA_IS_ANIMATED = "false";
	var TOA_MOUSEOVER = "";
	var TOA_MOUSEOUT = "";

	var TOA_CALLBACK_FUNC='';

	var ad_content = $("#home_custom_ad_content").html();

	if ( $.browser.msie ) {
		$("#home_custom_ad_content").attr("align", "center");
	}

	if (!ad_content.match('empty.gif') && trim(ad_content) != "") {
		initPromoAd(TOA_AD_STYLE, TOA_AD_HEIGHT, TOA_IS_ANIMATED, TOA_MOUSEOVER, TOA_MOUSEOUT, TOA_CALLBACK_FUNC);
	}
});


