$(document).ready(function () {
	var TOA_AKAMAI_PATH = getAdAkamaiPath();

	fb_pushCommand(function() {
		var isDailySpin = false;
		if (fb_logged_in) {
			var fbAdId = $.cookie("TFPROMO");

			if ($("body").html().indexOf("popUpDailySpin") != -1) {
				isDailySpin = true;
			}

			if (!fbAdId && !isDailySpin) {
				$.cookie("TFPROMO", "1", {expires: 30, path: '/'});
				var html = "<a class='button button_small_grey button_small_grey_close floatright' href='javascript:void(0)' onclick='closePopup()'></a>";
				html += "<a style='position: relative; top: 5px;' href='http://apps.facebook.com/feevo-game/' onclick='closePopup()' target='_blank'><img border='0' src='http://videoads.tetrisfriends.com/inHouse_ads/feevo_FBTF.jpg' /></a>";
				popUpBoxByHtml(html, 400, 340, "", function () {}, function () {});
			}
		}
	});
});


$(document).ready(function () {
	var TOA_AKAMAI_PATH       = "";
	var TOA_LEFT_RAIL_BG      = "transparent";
	var TOA_LEFT_RAIL_URL     = "http://videoads.tetrisfriends.com/inHouse_ads/feevoRailLeft.jpg";
	var TOA_LEFT_RAIL_REPEAT  = "no-repeat";
	var TOA_LEFT_RAIL_POS     = "-91,-303,302,1000";
	var TOA_RIGHT_RAIL_BG     = "transparent";
	var TOA_RIGHT_RAIL_URL    = "http://videoads.tetrisfriends.com/inHouse_ads/feevoRailRight.jpg";
	var TOA_RIGHT_RAIL_REPEAT = "no-repeat";
	var TOA_RIGHT_RAIL_POS    = "-91,-303,302,1000";
	var TOA_RAIL_CALLBACK     = "";

	var TOA_LEFT_RAIL_CONCAT  = TOA_LEFT_RAIL_BG + " url(" + TOA_AKAMAI_PATH + TOA_LEFT_RAIL_URL + ") " + TOA_LEFT_RAIL_REPEAT;
	var TOA_RIGHT_RAIL_CONCAT = TOA_RIGHT_RAIL_BG + " url(" + TOA_AKAMAI_PATH + TOA_RIGHT_RAIL_URL + ") " + TOA_RIGHT_RAIL_REPEAT;

	initRailAds(TOA_LEFT_RAIL_CONCAT, TOA_LEFT_RAIL_POS, TOA_RIGHT_RAIL_CONCAT, TOA_RIGHT_RAIL_POS, TOA_RAIL_CALLBACK);


	fb_pushCommand(function() {
		var isDailySpin = false;
		var fbAdId = $.cookie("TFPROMO");

		if ($("body").html().indexOf("popUpDailySpin") != -1) {
			isDailySpin = true;
		}

		if (!fbAdId && !isDailySpin) {
			$.cookie("TFPROMO", "1", {expires: 1, path: '/'});
			var html = "<a class='button button_small_grey button_small_grey_close floatright' href='javascript:void(0)' onclick='closePopup()'></a>";
			html += "<a style='position: relative; top: 5px;' href='javascript:void(0)' onclick='var url = \"http://itunes.apple.com/app/feevo-hd/id407806835?mt=8\"; window.open(url); try { pageTracker._trackPageview(\"/promotion/feevo_hd_popup/click\"); } catch (err) { } closePopup();'><img border='0' src='http://videoads.tetrisfriends.com/inHouse_ads/feevo_iPad_PopUp-2.png' /></a>";
			popUpBoxByHtml(html, 400, 340, "", function () {}, function () {});

			try { 
				pageTracker._trackPageview('/promotion/feevo_hd_popup'); 
			} catch (err) { } 
		}
	});
});


